mapper.select:
select
  seat_id, room_id, name, repair, x, y, direction, width, height
from library_seat


        and seat_id = #{seatId}


        and room_id = #{roomId}


        and name = #{name}


        and repair = #{repair}


        and x = #{x}


        and y = #{y}


        and direction = #{direction}


        and width = #{width}


        and height = #{height}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from library_seat


        and seat_id = #{seatId}


        and room_id = #{roomId}


        and name = #{name}


        and repair = #{repair}


        and x = #{x}


        and y = #{y}


        and direction = #{direction}


        and width = #{width}


        and height = #{height}
