mapper.select:
select
table_id, room_id, name, x, y, direction, width, height
from library_table


        and table_id = #{tableId}


        and room_id = #{roomId}


        and name = #{name}


        and x = #{x}


        and y = #{y}


        and direction = #{direction}


        and width = #{width}


        and height = #{height}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from library_table


        and table_id = #{tableId}


        and room_id = #{roomId}


        and name = #{name}


        and x = #{x}


        and y = #{y}


        and direction = #{direction}


        and width = #{width}


        and height = #{height}
