mapper.select:
select
user_id, school_id, library_id, room_id, seat_id, open
from receive_fast


        and user_id = #{userId}


        and school_id = #{schoolId}


        and library_id = #{libraryId}


        and room_id = #{roomId}


        and seat_id = #{seatId}


        and open = #{open}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from receive_fast


        and user_id = #{userId}


        and school_id = #{schoolId}


        and library_id = #{libraryId}


        and room_id = #{roomId}


        and seat_id = #{seatId}


        and open = #{open}
