mapper.select:
select
user_id, openid, session_key, avatar_url, city, province, country, gender, language, nick_name, real_name, age,
stu_id, background
from user_info


        and user_id = #{userId}


        and openid = #{openid}


        and session_key = #{sessionKey}


        and avatar_url = #{avatarUrl}


        and city = #{city}


        and province = #{province}


        and country = #{country}


        and gender = #{gender}


        and language = #{language}


        and nick_name = #{nickName}


        and real_name = #{realName}


        and age = #{age}


        and stu_id = #{stuId}


        and background = #{background}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user_info


        and user_id = #{userId}


        and openid = #{openid}


        and session_key = #{sessionKey}


        and avatar_url = #{avatarUrl}


        and city = #{city}


        and province = #{province}


        and country = #{country}


        and gender = #{gender}


        and language = #{language}


        and nick_name = #{nickName}


        and real_name = #{realName}


        and age = #{age}


        and stu_id = #{stuId}


        and background = #{background}
